    "approverTitle": "Chief Information Security Officer",
    "comments": "Approved after review. All requirements met."
}

{
    "name": "Monthly Compliance Status Report",
    "type": "compliance_summary",
    "description": "Monthly report showing compliance status across all frameworks",
    "format": "excel",
    "filter": {
        "frameworkIds": ["{{frameworkId}}"],
        "startDate": "2023-01-01T00:00:00.000Z",
        "endDate": "2023-12-31T00:00:00.000Z"
    },
    "schedule": {
        "frequency": "monthly",
        "hour": 1,
        "minute": 0,
        "dayOfMonth": 1,
        "recipients": ["admin@example.com", "compliance@example.com"]
    }
}

{
    "name": "Compliance Overview",
    "type": "compliance",
    "description": "Dashboard showing key compliance metrics and status",
    "isDefault": true,
    "widgets": [
        {
            "id": "widget1",
            "title": "Framework Implementation Status",
            "type": "chart",
            "dataSource": "/api/compliance/frameworks/stats",
            "size": {
                "width": 6,
                "height": 4
            },
            "position": {
                "x": 0,
                "y": 0
            },
            "config": {
                "chartType": "bar",
                "xAxis": "framework",
                "yAxis": "implementationRate"
            }
        },
        {
            "id": "widget2",
            "title": "Evidence Status",
            "type": "chart",
            "dataSource": "/api/compliance/evidence/stats",
            "size": {
                "width": 6,
                "height": 4
            },
            "position": {
                "x": 6,
                "y": 0
            },
            "config": {
                "chartType": "pie",
                "series": "status"
            }
        }
    ]
}

{
    "name": "Overall Compliance Score",
    "type": "compliance_score",
    "description": "Overall compliance score across all frameworks",
    "calculationMethod": "average",
    "query": "SELECT AVG(implementation_rate) FROM framework_stats",
    "unit": "%",
    "thresholds": {
        "critical": 70,
        "warning": 85,
        "target": 95
    }
}

{
    "name": "2023 SOC 2 Annual Audit",
    "type": "external",
    "description": "Annual SOC 2 Type II compliance audit",
